package com.fr.form.main;import com.fr.dashuju.Env;import com.fr.dashuju.ModuleContext;import com.fr.general.FRLogManager;import com.fr.general.Inter;import com.fr.module.FormModule;import java.io.FileNotFoundException;import java.io.InputStream;import java.io.OutputStream;public class FormIO{  public static Form readForm(Env paramEnv, String paramString)    throws Exception  {    if (paramString == null)      return null;    FRLogManager.declareResourceReadStart(paramString);    ModuleContext.startModule(FormModule.class.getName());    Form localForm = new Form();    try    {      InputStream localInputStream = paramEnv.readBean(paramString, "reportlets");      if (localInputStream == null)        throw new FileNotFoundException(Inter.getLocText("Cannot_Found_Template_File") + ":" + paramString);      localForm.readStream(localInputStream);      localInputStream.close();    }    catch (Exception localException)    {      throw FRLogManager.createLogPackedException(localException);    }    finally    {      FRLogManager.declareResourceReadEnd();    }    return localForm;  }  public static boolean writeForm(Env paramEnv, Form paramForm, String paramString)    throws Exception  {    FRLogManager.declareResourceWriteStart(paramString);    ModuleContext.startModule(FormModule.class.getName());    try    {      OutputStream localOutputStream = paramEnv.writeBean(paramString, "reportlets");      paramForm.export(localOutputStream);      localOutputStream.flush();      localOutputStream.close();    }    catch (Exception localException)    {      throw FRLogManager.createLogPackedException(localException);    }    finally    {      FRLogManager.declareResourceWriteEnd();    }    return true;  }}